import { Component } from '@angular/core';

@Component({
  selector: 'app-landing-page',
  templateUrl: './landing-page.component.html',
  styleUrls: ['./landing-page.component.less'],
})
export class LandingPageComponent {

  wallpaper = {
    url: "url('../../../assets/images/wallpaper/pokeballs.jpg",
    left: '',
    top: ''
  }

}
<!-- <testing-requestor-button></testing-requestor-button> -->
<hamburger></hamburger>
<hero-image></hero-image>
<div id="heroStatementsWrapper">
    <call-to-action></call-to-action>
    <login-register></login-register>
</div>
<background [imageUrl]="wallpaper.url" [left]="wallpaper.left" [top]="wallpaper.top"></background>
